import React, { useState, useEffect } from 'react';
import moment from 'moment-timezone';

// Replace these with your actual image URLs
const yourImageUrl = 'path/to/your/image.jpg';
const herImageUrl = 'path/to/her/image.jpg';

const LoveWebsite = () => {
    const [dublinTime, setDublinTime] = useState('');
    const [melbourneTime, setMelbourneTime] = useState('');

    useEffect(() => {
        // Function to fetch and set the current time in both time zones
        const fetchTime = () => {
            const dublinTimeNow = moment().tz('Europe/Dublin').format('HH:mm:ss');
            const melbourneTimeNow = moment().tz('Australia/Melbourne').format('HH:mm:ss');
            setDublinTime(dublinTimeNow);
            setMelbourneTime(melbourneTimeNow);
        };

        // Fetch the time initially
        fetchTime();

        // Update the time every minute
        const interval = setInterval(fetchTime, 60000); // Update every minute

        // Clean up interval on component unmount
        return () => clearInterval(interval);
    }, []);

    return (
        <div>
            <h1>Our Love Website</h1>
            <div className="image-container">
                <div>
                    <h2>Your Love</h2>
                    <img src={yourImageUrl} alt="You and Her" />
                    <p>Current Time in Dublin, Ireland: {dublinTime}</p>
                </div>
                <div>
                    <h2>Her Love</h2>
                    <img src={herImageUrl} alt="You and Her" />
                    <p>Current Time in Melbourne, Australia: {melbourneTime}</p>
                </div>
            </div>
        </div>
    );
};

export default LoveWebsite;
